var i = 0;

function guid(prefix) {
    if (typeof prefix === 'undefined') {
        prefix = '';
    } else {
        prefix = String(prefix);
    }

    return prefix + i++;
}

function uuid() {
    return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
        var r = Math.random() * 16 | 0;
        var v = c === 'x' ? r : r & 0x3 | 0x8;
        return v.toString(16);
    }).toUpperCase();
}

export { guid, uuid };
