'use strict';

var type = require('@jsmini/type');

function isNumber(x) {
    return type.type(x) === 'number';
}

function isBoolean(x) {
    return type.type(x) === 'boolean';
}

function isString(x) {
    return type.type(x) === 'string';
}

function isNull(x) {
    return type.type(x) === 'null';
}

function isUndefined(x) {
    return type.type(x) === 'undefined';
}

function isObject(x) {
    return type.type(x) === 'object';
}

function isFunction(x) {
    return type.type(x) === 'function';
}

const isArray = isFunction(Array.isArray) ? Array.isArray : function isArray(x) {
    return type.type(x) === 'array';
};

exports.isNumber = isNumber;
exports.isBoolean = isBoolean;
exports.isString = isString;
exports.isNull = isNull;
exports.isUndefined = isUndefined;
exports.isObject = isObject;
exports.isFunction = isFunction;
exports.isArray = isArray;
